const eventBus = {
  task: [],
  on(name, callback) {
    const task = this.tasks.find(item => item.name === name);
    if (task) {
      task.handlers.push(callback);
    } else {
      this.tasks.push({ name, handlers: [callback] });
    }
  },
  emit(name, ...args) {
    const task = this.tasks.find(item => item.name === name);
    if (task) {
      task.handlers.forEach(callback => {
        callback(...args);
      });
    }
  },
};

